import { Component } from '@angular/core';

@Component({
  selector: 'app-user',
  templateUrl: './user.component.html',
  styleUrls: ['./user.component.css'],
})
export class UserComponent {
  name = 'SAGAR';
  Id: number = 10;
  isnewuserAllowed = false;
  isUserCreated = 'No Users Found';
  userName = '';
  /**
   *
   */
  constructor() {
    setTimeout(() => {
      this.isnewuserAllowed = true;
    }, 3000);
  }

  getCreatedStatus() {
    this.isUserCreated = 'User Created Successfully';
  }

  updateData(event: Event) {
    this.userName = (event.target as HTMLInputElement).value;
  }
}
<p>user works!</p>
<h2>Hello {{ name }}</h2>

<hr />

<h2>String Interpolation</h2>
<p>Hello {{ name }}, Your Id is {{ Id }}</p>

<hr />

<h2>Property Binding</h2>
<button class="btn btn-primary" [disabled]="!isnewuserAllowed">
  Add New User
</button>

<hr />

<h2>Event Binding</h2>
<button
  class="btn btn-primary"
  [disabled]="!isnewuserAllowed"
  (click)="getCreatedStatus()"
>
  Add User
</button>
<p>User Created Status : {{ isUserCreated }}</p>

<hr />
<h2>using $event</h2>
<div class="row">
    <div class="col-md-4">
        <input type="text" class="form-control" (input)="updateData($event)" />
    </div>

    {{userName}}
</div>
